  mounted() {
    // this.dd();
    this.visualTxtAni();
    this.worksTitleAni();
    this.worksItemAni();
    this.imgScaleChange();
    this.bgChagne();
    this.textAnimation();
    this.introAni();
    this.goTopBtn();
    this.cursorDot();
    this.scrollAnimation();
    this.rollingTitleAni();
  },
  beforeUnmount() {
    clearInterval(this.intervalId);
  },
  methods: {
    visualTxtAni() {
      const tl = gsap.timeline({ paused: true });

      ScrollTrigger.create({
        trigger: ".visual-section",
        start: "top 80%",
        onEnter: () => {
          gsap.to(".visual-section", { opacity: 1, duration: 0 });
          tl.restart(true, false);
        },
        onEnterBack: () => {
          gsap.to(".visual-section", { opacity: 1, duration: 0 });
          tl.restart(true, false);
        },
        onLeave: () => {
          gsap.to(".visual-section", { opacity: 0, duration: 0.4 });
        }
      });

      tl.set(".visual-section", { opacity: 1 });

      tl.to(".visual-section .txt", {
        y: 0,
        opacity: 1,
        duration: 0.3,
        stagger: 0.03,
        ease: "power2.out"
      }, 0);

      tl.fromTo(".visual-line, .visual-arrow", {
        x: '-100%',
      }, {
        x: 0,
        duration: 0.3,
        ease: "linear"
      }, 0);

      tl.fromTo(".visual-icon", {
        x: '-100%',
      }, {
        x: 0,
        duration: 0.3,
        ease: "linear"
      }, 0);

      tl.fromTo(".visual-video-inner", {
        x: '-100%',
      }, {
        x: 0,
        duration: 0.3,
        ease: "linear"
      }, 0.1);

      // 멈춤
      tl.to({}, { duration: 1.2 });


      // 아이콘 회전 애니메이션
      gsap.to(".visual-section .ani-spin02", {
        xPercent: 0,
        duration: 1.2,
        ease: "power2.out",
        scrollTrigger: {
          trigger: ".visual-section",
          start: "top 80%",
          toggleActions: "play none none reverse",
        }
      });
    },

    worksTitleAni() {
      ScrollTrigger.create({
        trigger: ".works-section",
        start: "top 80%",
        onEnter: () => {
          document.querySelector(".works-section .line").classList.add("is-active");
        },
        onLeaveBack: () => {
          document.querySelector(".works-section .line").classList.remove("is-active");
        },
        // markers: true
      });
    },

    // 스크롤 시 works-item 살짝 올라오는 애니메이션
    worksItemAni() {
      gsap.utils.toArray(".works-item").forEach((item, index) => {
        gsap.to(item, {
          yPercent: -200,
          ease: "none",
          scrollTrigger: {
            trigger: ".works-section",
            start: "top 30%",
            end: "bottom 30%",
            scrub: true
          },
          delay: index * 1,
        });
      });
    },

    // 이미지 스케일 변경 (잘안됨)
    imgScaleChange() {
      gsap.to(".text-img", {
        scrollTrigger: {
          trigger: ".text-title-grp",
          start: "bottom 30%",
          scrub: true,
          pin: true,
        },
        autoAlpha: 1,
        width: "1500px",
        height: "800px",
        ease: "sine.out",
      });
    },
    // 스크롤 시 배경색 바꾸기
